%-----------------------寻找反向重复序列，满足:至少有segLen个连续正确配对的碱基;动态规划后，正确配对的碱基比率>=prob,间隔长度<=spacer------

%    seq='';
[~,seq]=fastaread('F:\基因组\Saccharomyces cerevisiae\Saccharomyces cerevisiae S288c mitochondrion.fasta');
L=length(seq);
seqNum=zeros(L,1);%------------数组初始化；
seqNpos=cell(3,50);Npos=0;
for seqN=1:L
   if seq(seqN)=='N';%------------将含N的位置记录下来--------------
        Npos=Npos+1;
        seqNpos{Npos}=seqN;
   end
end
xlswrite('F:\基因组\Saccharomyces cerevisiae\Saccharomyces cerevisiae S288c mitochondrion\Saccharomyces cerevisiae S288c mitochondrion-Npos.xlsx',seqNpos);  

for seqN=1:L
   seqNum(seqN)=convert(seq(seqN));%-------------按顺序正向编码：C=0,T=1,A=2,G=3；
end

segLen=6;gap1=1;gap2=1;prob=3/4;jumpIni=ceil(24*(1-prob));spacer=50;spaceMulti=4/3;
perLen=100000;cycleN=floor(L/perLen);%---------------以0.1 M为单位%------------循环次数

if cycleN<=1     %--------------------0.2M以下，运行一次即可输出--------
   [sumI,~]=IRFinderS(seqNum,segLen,spacer,spaceMulti,prob,gap1,gap2,jumpIni);%----------------simpleSum:反向重复位置，~为长度为segLen的回文序列--
   outS=outputSum(sumI);%---------化为单行输出--
   outS=lastClear(outS);   %--------------------结果中出现了前后互补位置刚好相反的，除去---
   xlswrite('F:\基因组\Saccharomyces cerevisiae\Saccharomyces cerevisiae S288c mitochondrion\Saccharomyces cerevisiae S288c mitochondrion-invSeq.xlsx',outS);      
else   
% ------------------------------
sumI=cell(1,1);sumII=cell(1,1);sumII{1,1}=0;contig=3*spacer;contigN=ceil(contig/segLen);
  for i=1:cycleN%cycleN-1:cycleN
    startHead=i*perLen-perLen;
    if i<cycleN
        seqNumPer=seqNum(startHead+1:i*perLen+contig);   %-----------分而治之----
    else
        seqNumPer=seqNum(startHead+1:end); %------------最后一段至底-----
    end
    [sumI,~]=IRFinderS(seqNumPer,segLen,spacer,spaceMulti,prob,gap1,gap2,jumpIni);%----------------simpleSum:反向重复位置，~为长度为segLen的回文序列---
    
    [head,~]=size(sumI);
    for headN=1:head
        [rev,~]=size(sumI{headN,2});
        for revN=1:rev
                sumI{headN,1}=sumI{headN,1}+startHead;
                sumI{headN,2}=sumI{headN,2}+startHead;
        end
    end
    
    if head>contigN  %----------比较的潜在重叠位置的个数
        contigNCI=contigN;
    else
        contigNCI=head-2;
    end
 
    if i>1 %--------当sumII未赋值时重叠位置不比较---
        for comII=headII-contigNCII:headII
            [revII,~]=size(sumII{comII,2});%-------
            for revIIN=1:revII
                if sumII{comII,2}(revIIN,1)>0
                    for comI=1:contigNCI
                        [revI,~]=size(sumI{comI,2});
                        for revIN=1:revI   %-------------被包含的位置去掉----
                            if sumI{comI,2}(revIN,1)>0
                                if sumII{comII,1}(revIIN,1)<=sumI{comI,1}(revIN,1) && sumII{comII,1}(revIIN,2)>=sumI{comI,1}(revIN,2)||sumII{comII,2}(revIIN,1)<=sumI{comI,2}(revIN,1)&&sumII{comII,2}(revIIN,2)>=sumI{comI,2}(revIN,2)
                                    sumI{comI,2}(revIN,1)=0;   %------------II包含I
                                elseif sumI{comI,1}(revIN,1)<=sumII{comII,1}(revIIN,1)&& sumI{comI,1}(revIN,2)>=sumII{comII,1}(revIIN,2)||sumI{comI,2}(revIN,1)<=sumII{comII,2}(revIIN,1)&&sumI{comI,2}(revIN,2)>=sumII{comII,2}(revIIN,2)
                                    sumII{comII,2}(revIIN,1)=0; %-----------I包含II
                                end
                            end
                        end
                    end
                end
            end
        end
%--------------------sumII被合并完成--------
        outS=outputSumNoZero(sumII);%---------去除非0项，化为单行输出--
        outS=lastClear(outS);     %--------------------结果中出现了前后互补位置刚好相反的，除去---
        xlswrite(strcat('F:\基因组\Saccharomyces cerevisiae\Saccharomyces cerevisiae S288c mitochondrion\Saccharomyces cerevisiae S288c mitochondrion-invSeq',num2str(i-1),'.xlsx'),outS);%--------输出上次位置
        
%------------------最后一次把sumI非0项输出------------
        if i==cycleN
            outS=outputSumNoZero(sumI);%---去除非0项，化为单行输出--
            outS=lastClear(outS);     %--------------------结果中出现了前后互补位置刚好相反的，除去---
            xlswrite(strcat('F:\基因组\Saccharomyces cerevisiae\Saccharomyces cerevisiae S288c mitochondrion\Saccharomyces cerevisiae S288c mitochondrion-invSeq',num2str(i),'.xlsx'),outS);%--------输出最后一次位置
        end
    end
    sumII=sumI;%---------记录上一循环的位置-------
    contigNCII=contigNCI;headII=head;
  end
end

